import {
  SET_PROFILE,
  REMOVE_PROFILE,
  SET_TOKEN,
  REMOVE_TOKEN,
} from "../actions/userActions";

const initialState = {
  profile: null,
  token: null,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_PROFILE: {
      return {
        ...state,
        profile: action.payload,
      };
    }
    case REMOVE_PROFILE: {
      return {
        ...state,
        profile: null,
      };
    }
    case SET_TOKEN: {
      return {
        ...state,
        token: action.payload,
      };
    }
    case REMOVE_TOKEN: {
      return {
        ...state,
        token: null,
      };
    }
    default:
      return state;
  }
};

export default reducer;
